  #endif
  #if CC_MORPH_TARGET_HAS_NORMAL
    attr.normal.xyz = attr.normal.xyz + getNormalDisplacement(vertexId);
  #endif
  #if CC_MORPH_TARGET_HAS_TANGENT
    attr.tangent.xyz = attr.tangent.xyz + getTangentDisplacement(vertexId);
  #endif
