  {
  #if !USE_INSTANCING
    v_luv.xy = cc_lightingMapUVParam.xy + a_texCoord1 * cc_lightingMapUVParam.zw;
    v_luv.z = cc_lightingMapUVParam.z;
  #else
    v_luv.xy = a_lightingMapUVParam.xy + a_texCoord1 * a_lightingMapUVParam.zw;
    v_luv.z = a_lightingMapUVParam.z;
  #endif
  }
#endif
