  uv = computeUV(a_texCoord.z, a_texCoord.xy, frameTile_velLenScale.xy) * mainTiling_Offset.xy + mainTiling_Offset.zw;
  pos = cc_matViewProj * pos;
  return pos;
}
void main() { gl_Position = lpvs_main(); }

